#ifndef QUICKGUIBORDERENUMS_H
#define QUICKGUIBORDERENUMS_H

namespace QuickGUI
{
	/**
	* Used to get or set properties aimed at a specific border side.
	*/
	enum BorderSide
	{
		BORDER_BOTTOM		=  0,
		BORDER_LEFT				,
		BORDER_RIGHT			,
		BORDER_TOP				,
		BORDER_BOTTOM_LEFT		,
		BORDER_BOTTOM_RIGHT		,
		BORDER_TOP_LEFT			,
		BORDER_TOP_RIGHT		,
		BORDER_NONE
	};
}

#endif
